// LandingPage.jsx
import React from 'react';
import '../global.css';
import './LandingPage.css'; // New CSS file for background
import { colors, fonts } from '../theme/theme';

import p1 from '../assets/p1.svg';
import a1 from '../assets/a1.svg';
import ap1 from '../assets/ap1.svg';
import m1 from '../assets/m1.svg';
import n1 from '../assets/n1.svg';
import g1 from '../assets/g1.svg';
import ServiceBentoGrid from './ServiceBentoGrid';
import PricingPlans from './PricingPlans';
import bgImage from '../assets/bg.png';
import logo from '../assets/logo.png';

const companies = [p1, a1, ap1, m1, n1, g1];

const HeroSection = () => (
  <section className="hero-section">
    <div className="hero-text">
      <h1>
        The Smartest Way to <span className="highlight">Search</span> Everything.
      </h1>
      <p className="hero-subtext">
        Use AI to find anything, anywhere — instantly. Relevant results in milliseconds.
      </p>
      <div className="hero-buttons">
        <a href="#search" className="hero-button">Search Now</a>
      </div>
    </div>
  </section>
);

const TrustedCompanies = () => (
  <div className="trusted-companies">
    {companies.map((logo, index) => (
      <img key={index} src={logo} alt="Trusted Company" />
    ))}
  </div>
);

const Footer = () => (
  <footer className="footer">
    <p>© 2025 AtomAI — Built with ❤️ for faster search</p>
    <div>
      <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="currentColor">
        <path d="M12 0L15.09 8H24L17.45 12.97L20.54 21L12 16.02L3.46 21L6.55 12.97L0 8H8.91L12 0Z" />
      </svg>
    </div>
  </footer>
);

const LandingPage = () => (
  <div className="page-wrapper">
    <HeroSection />
    <TrustedCompanies />
    <ServiceBentoGrid />
    <PricingPlans />
    <Footer />
  </div>
);

export default LandingPage;
