import React from 'react';
import '../global.css';
import { colors, fonts } from '../theme/theme';

const HeroSection = () => {
  return (
    <section style={{
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'flex-start',
      padding: '8rem 2rem 4rem 2rem',
      background: colors.background,
      color: colors.text,
      position: 'relative',
      overflow: 'hidden',
    }}>
      {/* Optional logo placement */}
      {/* <img src={require('../assets/logo.png')} alt="Logo" className="logo" style={{ marginBottom: '2rem' }} /> */}
      <h1 style={{
        fontFamily: fonts.heading,
        fontWeight: 'bold',
        fontSize: '3.5rem',
        lineHeight: 1.1,
        margin: 0,
        maxWidth: '900px',
      }}>
        You need a <span style={{
          background: colors.accent,
          color: '#181818',
          padding: '0.2em 0.5em',
          borderRadius: '0.25em',
          display: 'inline-block',
          transform: 'rotate(-2deg)',
        }}>powerful</span> <br /> website template.
      </h1>
      <p style={{
        fontFamily: fonts.body,
        fontSize: '1.35rem',
        margin: '2rem 0 2.5rem 0',
        color: '#ccc',
        maxWidth: '600px',
      }}>
        Launch a stunning website that looks like you hired a top design agency. In days.
      </p>
      <div style={{ display: 'flex', gap: '1.5rem' }}>
        <a href="#pricing" style={{
          background: '#181818',
          color: colors.text,
          border: `2px solid ${colors.accent}`,
          borderRadius: '0.5rem',
          padding: '1rem 2.5rem',
          fontFamily: fonts.heading,
          fontWeight: 'bold',
          fontSize: '1.1rem',
          transition: 'background 0.2s, color 0.2s',
        }}>Browse Templates</a>
        <a href="#how-it-works" style={{
          background: colors.accent,
          color: '#181818',
          borderRadius: '0.5rem',
          padding: '1rem 2.5rem',
          fontFamily: fonts.heading,
          fontWeight: 'bold',
          fontSize: '1.1rem',
          boxShadow: '0 2px 8px 0 rgba(77,255,41,0.08)',
          transition: 'background 0.2s, color 0.2s',
        }}>Learn How It Works</a>
      </div>
    </section>
  );
};

export default HeroSection;
