import React, { useState, useRef, useEffect } from 'react';
import { FaBars, FaUserCircle, FaStar, FaChevronLeft, FaChevronRight, FaSearch, FaPlus, FaRegHeart } from 'react-icons/fa';

const GEMINI_API_KEY = 'YOUR_GEMINI_API_KEY'; // <-- Replace with your real key
const GEMINI_API_URL = 'https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=' + GEMINI_API_KEY;

const dummySidebar = [
  { icon: <FaPlus />, label: 'New chat' },
  { icon: <FaRegHeart />, label: 'Explore Gems' },
  { icon: <FaSearch />, label: 'Recent' },
  { icon: <FaChevronRight />, label: 'Show more' },
];

const dummyRecent = [
  'Friendly Greeting and Assist...',
  'Ready to Assist?',
  'Requesting Explicit Images: ...',
  'Cute Response:',
  'Bollywood: Overview and C...'
];

const Chat = () => {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);
  const [showHello, setShowHello] = useState(true);

  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages, loading]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setMessages([...messages, { from: 'user', text: input }]);
    setInput('');
    setShowHello(false);
    setLoading(true);
    try {
      const res = await fetch(GEMINI_API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          contents: [{ parts: [{ text: input }] }]
        })
      });
      const data = await res.json();
      let botReply = 'Sorry, no response.';
      if (data && data.candidates && data.candidates[0] && data.candidates[0].content && data.candidates[0].content.parts && data.candidates[0].content.parts[0].text) {
        botReply = data.candidates[0].content.parts[0].text;
      }
      setMessages(msgs => [...msgs, { from: 'bot', text: botReply }]);
    } catch (err) {
      setMessages(msgs => [...msgs, { from: 'bot', text: 'Error fetching response from Gemini API.' }]);
    }
    setLoading(false);
  };

  return (
    <div style={{ display: 'flex', height: '100vh', fontFamily: 'Helvetica, Arial, sans-serif', background: '#181818', color: '#fff' }}>
      {/* Sidebar */}
      <div style={{
        width: sidebarOpen ? 260 : 60,
        background: '#111',
        transition: 'width 0.2s',
        display: 'flex',
        flexDirection: 'column',
        borderRight: '1.5px solid #232323',
        minWidth: 0,
        zIndex: 2
      }}>
        <div style={{ display: 'flex', alignItems: 'center', padding: '1.2rem 1rem 1rem 1rem', gap: 10 }}>
          <FaBars style={{ cursor: 'pointer', color: '#4DFF29' }} onClick={() => setSidebarOpen(o => !o)} />
          {sidebarOpen && <span style={{ fontWeight: 700, fontSize: 22, marginLeft: 10, color: '#fff' }}>Gemini</span>}
        </div>
        {sidebarOpen && (
          <>
            <button style={{ background: 'none', border: 'none', color: '#fff', fontWeight: 600, fontSize: 16, display: 'flex', alignItems: 'center', gap: 8, padding: '0.7rem 1.2rem', cursor: 'pointer', width: '100%' }}>
              <FaPlus style={{ color: '#4DFF29' }} /> New chat
            </button>
            <button style={{ background: 'none', border: 'none', color: '#fff', fontWeight: 600, fontSize: 16, display: 'flex', alignItems: 'center', gap: 8, padding: '0.7rem 1.2rem', cursor: 'pointer', width: '100%' }}>
              <FaRegHeart style={{ color: '#4DFF29' }} /> Explore Gems
            </button>
            <div style={{ color: '#aaa', fontSize: 13, margin: '1.2rem 0 0.5rem 1.2rem' }}>Recent</div>
            <div>
              {dummyRecent.map((item, i) => (
                <div key={i} style={{ color: '#fff', fontSize: 15, padding: '0.5rem 1.2rem', borderRadius: 8, background: i === 0 ? '#222' : 'none', margin: '0.2rem 0', cursor: 'pointer', fontWeight: i === 0 ? 600 : 400 }}>
                  {item}
                </div>
              ))}
            </div>
            <button style={{ background: 'none', border: 'none', color: '#fff', fontWeight: 600, fontSize: 16, display: 'flex', alignItems: 'center', gap: 8, padding: '0.7rem 1.2rem', cursor: 'pointer', width: '100%' }}>
              <FaChevronRight style={{ color: '#4DFF29' }} /> Show more
            </button>
            <div style={{ position: 'absolute', bottom: 24, left: 0, width: '100%', textAlign: 'center', color: '#aaa', fontSize: 15 }}>
              <FaUserCircle size={28} style={{ marginRight: 8, color: '#fff' }} /> Settings & help
            </div>
          </>
        )}
      </div>
      {/* Main Chat Area */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column', height: '100vh', position: 'relative', background: '#181818', minWidth: 0 }}>
        {/* Top bar */}
        <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center', padding: '1.2rem 2rem 0.5rem 2rem', gap: 16 }}>
          <button style={{ background: '#232323', color: '#fff', border: 'none', borderRadius: 8, padding: '0.5rem 1.2rem', fontWeight: 600, fontSize: 15, cursor: 'pointer', boxShadow: '0 2px 8px #0004' }}>
            <FaStar style={{ color: '#4DFF29', marginRight: 6 }} /> Upgrade
          </button>
          <FaUserCircle size={36} style={{ color: '#fff', background: '#232323', borderRadius: '50%' }} />
        </div>
        {/* Chat area */}
        <div style={{ flex: 1, overflowY: 'auto', padding: '2.5rem 0 1rem 0', display: 'flex', flexDirection: 'column', alignItems: 'center', position: 'relative', width: '100%' }}>
          {showHello && messages.length === 0 && !loading && (
            <div style={{ color: '#fff', fontSize: 22, marginTop: 40, opacity: 0.7, fontWeight: 500 }}>
              <span style={{ color: '#4DFF29', fontWeight: 700 }}>Hello, User!</span>
              <div style={{ fontSize: 18, color: '#aaa', marginTop: 10 }}>Hi there! How can I help you today?</div>
            </div>
          )}
          <div style={{ width: '100%' }}>
            {messages.map((msg, i) => (
              <div key={i} style={{ display: 'flex', justifyContent: msg.from === 'user' ? 'flex-end' : 'flex-start', margin: '0.5rem 0' }}>
                <div style={{
                  background: msg.from === 'user' ? '#232323' : '#222',
                  color: msg.from === 'user' ? '#4DFF29' : '#fff',
                  borderRadius: 18,
                  padding: '0.7rem 1.3rem',
                  maxWidth: 600,
                  fontSize: 16,
                  boxShadow: msg.from === 'user' ? '0 2px 8px #4DFF2940' : '0 2px 8px #0004',
                  fontWeight: 500,
                  marginLeft: msg.from === 'user' ? 40 : 0,
                  marginRight: msg.from === 'user' ? 0 : 40,
                  wordBreak: 'break-word',
                }}>
                  {msg.text}
                </div>
              </div>
            ))}
            {loading && (
              <div style={{ display: 'flex', justifyContent: 'flex-start', margin: '0.5rem 0' }}>
                <div style={{
                  background: '#222',
                  color: '#4DFF29',
                  borderRadius: 18,
                  padding: '0.7rem 1.3rem',
                  maxWidth: 600,
                  fontSize: 16,
                  fontWeight: 500,
                  marginRight: 40,
                  display: 'flex',
                  alignItems: 'center',
                  gap: 8,
                }}>
                  <span className="dot-flashing" style={{ display: 'inline-block', width: 18, height: 18, position: 'relative' }}>
                    <span style={{
                      position: 'absolute',
                      left: 0,
                      top: 0,
                      width: 6,
                      height: 6,
                      borderRadius: '50%',
                      background: '#4DFF29',
                      animation: 'dotFlashing 1s infinite linear alternate',
                    }} />
                    <span style={{
                      position: 'absolute',
                      left: 8,
                      top: 0,
                      width: 6,
                      height: 6,
                      borderRadius: '50%',
                      background: '#4DFF29',
                      animation: 'dotFlashing 1s infinite linear alternate',
                      animationDelay: '0.3s',
                    }} />
                    <span style={{
                      position: 'absolute',
                      left: 16,
                      top: 0,
                      width: 6,
                      height: 6,
                      borderRadius: '50%',
                      background: '#4DFF29',
                      animation: 'dotFlashing 1s infinite linear alternate',
                      animationDelay: '0.6s',
                    }} />
                  </span>
                  <span>Gemini is thinking…</span>
                </div>
              </div>
            )}
            <div ref={chatEndRef} />
          </div>
        </div>
        {/* Input area */}
        <form onSubmit={handleSend} style={{ width: '100%', margin: '0 auto', padding: '0 0 2rem 0', display: 'flex', alignItems: 'center', gap: 12 }}>
          <input
            type="text"
            value={input}
            onChange={e => setInput(e.target.value)}
            placeholder="Ask Gemini"
            style={{
              flex: 1,
              background: '#232323',
              color: '#fff',
              border: '1.5px solid #333',
              borderRadius: 18,
              padding: '1.1rem 1.5rem',
              fontSize: 17,
              fontFamily: 'Helvetica, Arial, sans-serif',
              outline: 'none',
              boxShadow: '0 2px 8px #0004',
              transition: 'border 0.2s',
            }}
            autoFocus
          />
          <button type="submit" style={{
            background: 'linear-gradient(135deg, #4DFF29 60%, #9FFF62 100%)',
            color: '#181818',
            fontWeight: 700,
            fontSize: 17,
            border: 'none',
            borderRadius: 18,
            padding: '1.1rem 2.2rem',
            cursor: 'pointer',
            boxShadow: '0 4px 16px 0 #4dff2940, 0 1.5px 8px 0 #fff2, 0 2px 0 #2e7d1f',
            transition: 'transform 0.15s cubic-bezier(.17,.67,.83,.67), box-shadow 0.2s',
          }}>Send</button>
        </form>
        <style>{`
          @keyframes dotFlashing {
            0% { opacity: 0.2; }
            50%, 100% { opacity: 1; }
          }
        `}</style>
      </div>
    </div>
  );
};

export default Chat;
